import React, { useRef } from "react";


function SearchInput(props) {
    const {sendName} = props
    const input = useRef(null)

    return (
      <div class="input-group mb-3" style={{width:'80vw'}}>
        <input ref={input} type="text" class="form-control" placeholder="Escreva um sobrenome" aria-describedby="basic-addon2"/>
        <div class="input-group-append ml-3">
          <button class="btn btn-secondary" disabled={!!input.current.value} type="button" onClick={(e) => sendName(input.current.value)}>Enviar</button>
        </div>
      </div>
    );
  }

export default SearchInput
